// HACK: This is a hack to prevent the need to hardcode the url which is a
// problem when things sit in docker and you wish to be generic, yes we
// acknowledge the concerns...
export const BASE_URL = `${typeof window !== "undefined" ? window.location.origin : 'http://localhost:' + process.env.PORT}`;

export const SNAKE_API = typeof process !== "undefined" && process.env.SNAKE_SKIN_SNAKE_API ? `${process.env.SNAKE_SKIN_SNAKE_API}` : 'http://localhost:5000';
